import React, { useState, useEffect } from "react";
import "./App.css";
import NotificationForm from "./components/NotificationForm";
import Confirmation from "./components/Confirmation";
import axios from "axios";

function App() {
  const [alertData, setAlertData] = useState(null);
  const [cryptocurrencies, setCryptocurrencies] = useState([]);
  const [notificationMethod, setNotificationMethod] = useState(""); // Tracks dropdown selection
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [emailError, setEmailError] = useState(null);
  const [emailSuccess, setEmailSuccess] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

  // Fetch cryptocurrencies from backend
  const fetchCryptocurrencies = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/cryptocurrencies/`);
      setCryptocurrencies(response.data);
      setError(null);
    } catch (err) {
      console.error("Error fetching cryptocurrencies:", err);
      setError("Failed to fetch cryptocurrencies.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCryptocurrencies();
  }, []);

  const handleNotificationMethodChange = (e) => {
    setNotificationMethod(e.target.value);
    // Clear email if the user switches from Email to another method
    if (e.target.value !== "Email") {
      setEmail("");
    }
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setEmailSuccess(null);
    setEmailError(null);

    try {
      await axios.post(`${API_URL}/send-email`, { email });
      setEmailSuccess("Email sent successfully!");
    } catch (err) {
      console.error("Error sending email:", err);
      setEmailError("Failed to send the email. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (data) => {
    setAlertData(data);
    console.log("Alert Data:", data);
  };

  return (
    <div className="App">
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <button onClick={fetchCryptocurrencies} disabled={loading}>
          {loading ? "Fetching..." : "Fetch Cryptocurrencies"}
        </button>
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && (
        <>
          {/* Notification Method Dropdown */}
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="notification-method">Notification Method:</label>
            <select
              id="notification-method"
              value={notificationMethod}
              onChange={handleNotificationMethodChange}
            >
              <option value="">Select Method</option>
              <option value="Email">Email</option>
              <option value="SMS">SMS</option>
              <option value="Push Notification">Push Notification</option>
            </select>
          </div>

          {/* Dynamically Show Email Input */}
          {notificationMethod === "Email" && (
            <div style={{ marginTop: "20px" }}>
              <h2>Enter Your Email</h2>
              <form onSubmit={handleEmailSubmit}>
                <input
                  type="email"
                  placeholder="example@example.com"
                  value={email}
                  onChange={handleEmailChange}
                  required
                />
                <button type="submit" disabled={loading}>
                  {loading ? "Sending..." : "Send Email"}
                </button>
              </form>
              {emailSuccess && <p style={{ color: "green" }}>{emailSuccess}</p>}
              {emailError && <p style={{ color: "red" }}>{emailError}</p>}
            </div>
          )}

          {/* Notification Form */}
          <NotificationForm
            onSubmit={handleSubmit}
            cryptocurrencies={cryptocurrencies}
          />
        </>
      )}
      {alertData && (
        <Confirmation
          alertData={alertData}
          onModify={() => setAlertData(null)}
        />
      )}
    </div>
  );
}

export default App;
