import React, { useState } from "react";

function NotificationForm({ onSubmit, cryptocurrencies }) {
  const [notificationMethod, setNotificationMethod] = useState("");
  const [email, setEmail] = useState("");
  const [crypto, setCrypto] = useState("");
  const [threshold, setThreshold] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      notificationMethod,
      email: notificationMethod === "Email" ? email : null,
      crypto,
      threshold,
    };
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Set Notification</h2>

      {/* Cryptocurrency Dropdown */}
      <label>Select Cryptocurrency:</label>
      <select value={crypto} onChange={(e) => setCrypto(e.target.value)}>
        <option value="">Select</option>
        {cryptocurrencies.map((coin) => (
          <option key={coin.id} value={coin.id}>
            {coin.name}
          </option>
        ))}
      </select>

      {/* Threshold Input */}
      <label>Set Threshold:</label>
      <input
        type="number"
        placeholder="Enter threshold value"
        value={threshold}
        onChange={(e) => setThreshold(e.target.value)}
        required
      />

      {/* Notification Method Dropdown */}
      <label>Notification Method:</label>
      <select
        value={notificationMethod}
        onChange={(e) => setNotificationMethod(e.target.value)}
        required
      >
        <option value="">Select</option>
        <option value="Email">Email</option>
        <option value="SMS">SMS</option>
      </select>

      {/* Email Input - Dynamically Displayed */}
      {notificationMethod === "Email" && (
        <>
          <label>Enter Your Email:</label>
          <input
            type="email"
            placeholder="example@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </>
      )}

      <button type="submit">Set Alert!</button>
    </form>
  );
}

export default NotificationForm;
