// src/components/Confirmation.js
import React from 'react';
import '../styles/Confirmation.css';

const Confirmation = ({ alertData, onModify, onClose }) => {
  const handleDone = () => {
    // Show browser notification
    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          new Notification('Alert Set Successfully!', {
            body: 'You will be notified when your conditions are met.'
          });
        }
      });
    }

    // Close the modal
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h1>Alert Setup Complete!</h1>
        <div className="confirmation-details">
          <p>Cryptocurrency ID: {alertData.crypto_id}</p>
          <p>Notification Type: {alertData.notification_type}</p>
          <p>Threshold Value: {alertData.threshold_value}</p>
          <p>Notification Method: {alertData.notification_method}</p>
        </div>
        <div className="modal-buttons">
          <button className="modal-button" onClick={onModify}>
            Modify Settings
          </button>
          <button className="modal-button done-button" onClick={handleDone}>
            Done
          </button>
        </div>
      </div>
    </div>
  );
};

export default Confirmation;
